import classes from './MealsSummary.module.css';
const MealsSummary = () => {
 return <section className={classes.summary}>
    <h2>Delicious Food, Delivered to you</h2>
    <p>
        Choose your favorite meal from our broad selection of available meals and enjoy a delicious lunch and dinner with fast delivery at your home.
    </p>
    <p>
        All of our meals are cooked with high quality ingredients by our experienced chefs!
    </p>
 </section>
};
export default MealsSummary;
